sass:
$lightHover: #eff6ff;
$lightSelect: #F5F5F5;
$lightOffRow: #fafafa;
$border-m: 0.5rem;
@import url('https://fonts.googleapis.com/css2?family=Inter:ital,opsz,wght@0,14..32,100..900;1,14..32,100..900&display=swap');
@use 'sass:color';

* {
	font-family: "Inter",system-ui,sans-serif;
	padding: 0px;
	margin: 0px;

	--color-lighthover: $lightHover;
	--color-lightselect: $lightSelect;
	--color-lightoffrow: $lightOffRow;
	--color-forecolor: $foreColor;
	--color-forelightcolor: $foreLightColor;
	--color-foreverylightcolor: $foreVeryLightColor;
	--color-iconcolor: $iconColor;
	--color-bordercolor: $borderColor;

	--border-m: $border-m;
	--border-l: 1rem;
}

.layout-tabs-bar {
	display: flex;
	gap: $border-m;
	border-bottom: 1px solid $foreSubtleColor;
}

.layout-tabs-tab {
	display: flex;
	gap: 0.5rem;
	align-items: center;
	color: $iconColor;
	fill: $iconColor;
	stroke: $iconColor;
	padding: 0.5rem;
	transform: translateY(1px);
	cursor: pointer;
}

.layout-tabs-tabname, .layout-tabs-tabIcon {
	font-size: 1.1rem;
}

.layout-tabs-chosen {
	color: $accentColor;
	stroke: $accentColor;
	fill: $accentColor;
	border-bottom: 2px solid $accentColor;
	background-color: mix($accentColor, rgba(1, 1, 1, 0), 15%);
}

.layout-tabs-tab:hover:not(.layout-tabs-chosen) {
	background-color: mix($accentColor, rgba(1, 1, 1, 0), 5%);
}

.edit {
	border: 1px solid $borderColor;
	border-radius: $border-m;
	padding: 0.4rem 0.6rem;
}

.edit:focus {
	border: 2px solid $accentColor;
}

.list {
	border-radius: $border-m;
}

.listFocus {
	border: 1px solid $borderColor;
}

.listFooter {
	margin-top: auto;
}

.listColHeader {
	padding: 0px;
	color: $foreLightColor;
	font-size: 0.8rem;
	font-weight: bold;
	font-family: "Inter";
}

.listColHeaderItemHover {
	background-color: $lightSelect;
}

.listItemHover {
	background-color: $lightSelect;
	color: $foreColor;
}


.listItemHover .icon {
	stroke: $foreColor;
	fill: $foreColor;
}

.listItemSelected {
	background-color: $lightHover;
	color: $foreColor;
}

.listItemSelected .icon {
	stroke: $foreColor;
	fill: $foreColor;
}

.button, .buttonPrimary, .buttonIconOnly {
	cursor: pointer;
	color: $foreColor;
	border: 1px solid $borderColor;
	background-color: $lightSelect;
	font-weight: normal;
	border-radius: $border-m;
	padding: 0.3rem 0.6rem;
}

.buttonIconOnly { 
	background-color: transparent;
	border: 0px;
	color: $iconColor;
}

.buttonIconOnly .icon {
	stroke: $iconColor;
	fill: $iconColor;
}

.buttonPrimary {
	background-color: $accentColor;
	color: $backColor;
}

.compact .button {
	padding: 0.3rem 0.6rem;
}

.compact .buttonPrimary { 
	padding: 0.5rem 0.8rem;
}

.compact .buttonIconOnly { 
	padding: 0.3rem;
}

.button .icon, .buttonPrimary .button {
	stroke: $foreColor;
	fill: $foreColor;
}

.buttonHover, .listItemHover .button:hover {
	background-color: $lightHover;
	color: $accentColor;
	border: 1px solid $accentColor;
	color: $foreColor;
}

.buttonIconOnlyHover, .listItemHover .buttonIconOnly:hover {
	background-color: $lightSelect;
}

.buttonPrimaryHover, .listItemHover .buttonPrimary:hover { 
	background-color: mix($accentColor, rgb(0, 0, 0), 80%);
	color: $backColor;
}

.buttonHover .icon, .listItemHover .button:hover {
	fill: $accentColor;
	stroke: $accentColor;
}

.listItemSelected .button, .listItemSelected .buttonPrimary {
	background-color: $lightHover;
	color: $accentColor;
}


.buttonPressed, .buttonPrimaryPressed {
	background-color: $lightHover;
	color: $accentColor;
	font-weight: bold;
	border: 1px solid $accentColor;
}

.buttonIconOnlyPressed { 
	background-color: $lightSelect;
	font-weight: bold;
}

.buttonIconOnlyPressed .icon {
	fill: $foreColor;
	stroke: $foreColor;
}

.buttonPrimaryPressed { 
	background-color: mix($accentColor, rgb(0, 0, 0), 80%);
	color: $backColor;
	border: 1px solid transparent;
}

.buttonDisabled, .buttonPrimaryDisabled {
	background-color: $lightSelect;
	color: $foreLightColor;
	cursor: not-allowed;
}

.buttonIconOnlyDisabled { 	cursor: not-allowed; }

.buttonIconOnlyDisabled .icon {
	fill: $foreLightColor;
	stroke: $foreLightColor;
}

.dynamic-form-simple-label {
	margin: 0px;
}

.editDisabled {
	cursor: not-allowed;
	background-color: $lightSelect;
	color: $foreLightColor;
}

.editDisabled:focus, .compact .editDisabled:focus {
	background-size: 100% 0px;
	outline: 0px solid transparent;
	border: 1px solid $borderColor;
}

.edit:focus, .compact .edit:focus {
	background-size: 100% 0px;
	outline: 1px solid $accentColor;
	border: 1px solid $accentColor;
}

.embeddedContainer {
	height: 100%;
}
